import { CART_TYPE } from "./cart.type";

export const toggleCartHidden = () => ({
  type: CART_TYPE.TOGGLE_CART_HIDDEN,
});

export const addItem = (item) => ({
  type: CART_TYPE.ADD_ITEM,
  payload: item,
});
