import { SET_CURRENT_USER } from "./user.type";

const INITIAL_STATE = {
  currentUser: null,
};
const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SET_CURRENT_USER:
      return { ...state, user: { ...state.user, currentUser: action.payload } };
    default:
      return state;
  }
};

export default userReducer;
