import { TOGGLE_CART_HIDDEN } from "./cart.type";

const INITIAL_STATE = {
  hiddenCart: true,
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case TOGGLE_CART_HIDDEN:
      return {
        ...state,
        hiddenCart: !state.hiddenCart,
      };
    default:
      return INITIAL_STATE;
  }
};

export default cartReducer;
