const { TOGGLE_HIDDEN_CART } = require("./hidden.type");

const INITIAL_STATE = {
  hiddenCart: false,
};

const hiddenReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case TOGGLE_HIDDEN_CART:
      return {
        ...state,
        hiddenCart: !state.hiddenCart,
      };
    default:
      return INITIAL_STATE;
  }
};

export default hiddenReducer;
