import React from "react";
import { connect } from "react-redux";
import { Route } from "react-router-dom";
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import { getCollections } from "../../redux/shop/shop.action";
import CollectionPage from "../collection/collection.component";
import { firestore, getCollectionData } from "../../firebase/firebase.utils";

class ShopPage extends React.Component {
  componentDidMount() {
    const collections = getCollectionData();
    console.log(collections);
    this.props.setCollections(collections);
  }

  render() {
    const { match } = this.props;
    return (
      <div className="shop-page">
        <Route exact path={`${match.path}`} component={CollectionsOverview} />
        <Route
          path={`${match.path}/:collectionId`}
          component={CollectionPage}
        />
      </div>
    );
  }
}

const mapDispatch = (dispatch) => ({
  setCollections: (collections) => dispatch(getCollections(collections)),
});

export default connect(null, mapDispatch)(ShopPage);
