import { TOGGLE_HIDDEN_CART } from "./hidden.type";

const INITIAL_STATE = {
  hiddenCart: true,
};

const hiddenReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case TOGGLE_HIDDEN_CART:
      return {
        ...state,
        hiddenCart: false,
      };
    default:
      return INITIAL_STATE;
  }
};

export default hiddenReducer;
