import React from "react";
import { connect } from "react-redux";
import CollectionItem from "../../components/collection-item/collection-item.component";
import { selectCollections } from "../../redux/shop/shop.selectors";
import "./collection.styles.scss";

const Collection = ({ match, collections }) => (
  <div className="colletion">
    <h2 className="collection-title"> {match.params.collectionId}</h2>

    <div className="collection-items">
      {collections
        .filter(
          (collection) => collection.routeName === match.params.collectionId
        )[0]
        .items.map((item) => (
          <CollectionItem key={item.id} item={item} />
        ))}
    </div>
  </div>
);

const mapState = (state) => ({
  collections: selectCollections(state),
});

export default connect(mapState)(Collection);
