import { ShopType } from "./shop.type";
import SHOP_DATA from "./shop_data";

const INITIAL_STATE = {
  collections: null,
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ShopType.GET_COLLECTIONS_DATA:
      return {
        ...state,
        collections: action.payload,
      };
    default:
      return state;
  }
};

export default shopReducer;
